import { useState } from "react";

function Search({ cb = Function.prototype }) {
  const [value, setValue] = useState("");

  const handleKey = (e) => {
    if (e.key === "Enter") {
      handleSubmit();
    }
  };
  const handleSubmit = () => {
      cd(value)
  };

  return (
    <div className="row container">
      <div className="input-field col s12">
        <input
          type="search"
          id="search-field"
          placeholder="Search"
          onKeyDown={handleKey}
          onChange={(e) => setValue(e.target.value)}
          value={value}
        />
        <button
          className="btn"
          onClick={handleSubmit}
          style={{
            position: "absolute",
            top: 0,
            right: 0,
          }}
        >
          Search
        </button>
      </div>
    </div>
  );
}
export { Search };
